import unittest

from aoc2022.day_06 import (
    part_one,
    part_two,
)


class DaySixTestCase(unittest.TestCase):
    def test_part_one_solves_example_input_1(self):
        result = part_one("mjqjpqmgbljsphdztnvjfqwrcgsmlb")

        self.assertEqual(result, 7)

    def test_part_one_solves_example_input_2(self):
        result = part_one("bvwbjplbgvbhsrlpgdmjqwftvncz")

        self.assertEqual(result, 5)

    def test_part_one_solves_example_input_3(self):
        result = part_one("nppdvjthqldpwncqszvftbrmjlhg")

        self.assertEqual(result, 6)

    def test_part_one_solves_example_input_4(self):
        result = part_one("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")

        self.assertEqual(result, 10)

    def test_part_one_solves_example_input_5(self):
        result = part_one("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")

        self.assertEqual(result, 11)

    def test_part_one_solves_real_input(self):
        result = part_one(
            ""
        )

        self.assertEqual(result, 1538)

    def test_part_two_solves_example_input_1(self):
        result = part_two("mjqjpqmgbljsphdztnvjfqwrcgsmlb")

        self.assertEqual(result, 19)

    def test_part_two_solves_example_input_2(self):
        result = part_two("bvwbjplbgvbhsrlpgdmjqwftvncz")

        self.assertEqual(result, 23)

    def test_part_two_solves_example_input_3(self):
        result = part_two("nppdvjthqldpwncqszvftbrmjlhg")

        self.assertEqual(result, 23)

    def test_part_two_solves_example_input_4(self):
        result = part_two("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")

        self.assertEqual(result, 29)

    def test_part_two_solves_example_input_5(self):
        result = part_two("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")

        self.assertEqual(result, 26)

    def test_part_two_solves_real_input(self):
        result = part_two(
            ""
        )

        self.assertEqual(result, 2315)
